declare(strict_types=1);

namespace Ibexa\Contracts\Connector\Dam;

final class AssetIdentifier
{
    /** @var string */
    private $id;

    public function __construct(string $id)
    {
        $this->id = $id;
    }

    public function getId(): string
    {
        return $this->id;
    }
}

class_alias(AssetIdentifier::class, 'Ibexa\Platform\Contracts\Connector\Dam\AssetIdentifier');
