declare(strict_types=1);

namespace Ibexa\Contracts\Connector\Dam;

final class AssetUri
{
    /** @var string */
    private $path;

    public function __construct(string $path)
    {
        $this->path = $path;
    }

    public function getPath(): string
    {
        return $this->path;
    }
}

class_alias(AssetUri::class, 'Ibexa\Platform\Contracts\Connector\Dam\AssetUri');
