use Symfony\Component\HttpFoundation\Request;

final class ModelListDataFetcher extends AbstractPersonalizationConsumer implements ModelListDataFetcherInterface, TranslationContainerInterface
{
    private const ENDPOINT_URI = '/api/v3/%d/structure/get_model_list';
    public const PARAM_MODEL_LIST = 'modelList';

    public function __construct(PersonalizationClientInterface $client, string $endPointUri)
    {
        parent::__construct($client, $endPointUri . self::ENDPOINT_URI);
    }

    public function fetchModelList(
        int $customerId,
        string $licenseKey
    ): ResponseInterface {
        $uri = $this->buildEndPointUri(
            [
                $customerId,
            ]
        );

        $this->setAuthenticationParameters($customerId, $licenseKey);

        return $this->client->sendRequest(
            Request::METHOD_GET,
            $uri,
            $this->getOptions()
        );
    }

