declare(strict_types=1);

namespace Ibexa\Personalization\Form\Data;

final class OptionalIntegerData
{
    /** @var bool */
    private $enabled;

    /** @var int|null */
    private $value;

    public function isEnabled(): bool
    {
        return $this->enabled;
    }

    public function setEnabled(bool $enabled): self
    {
        $this->enabled = $enabled;

        return $this;
    }

    public function getValue(): ?int
    {
        return $this->value;
    }

    public function setValue(?int $value): self
    {
        $this->value = $value;

        return $this;
    }
}

class_alias(OptionalIntegerData::class, 'Ibexa\Platform\Personalization\Form\Data\OptionalIntegerData');
